import { useSearchParams } from "react-router-dom";
import BasicLayout from "../../layouts/BasicLayout";
import ListComponent from "../../components/board/ListComponent";



const ListPage = () => {

    // 쿼리스트링 처리를 위한 useSearchParams()
    const [search, setSearch] = useSearchParams()

    // api server에서 보내준 pageResponse를 확인하기 위한 search.get()
    const page = search.get("page") || 1
    const size = search.get("size") || 10
    const type = search.get("type") || null
    const keyword = search.get("keyword") || null

    // page size type keyword check 
    const queryObj = {page,size,type,keyword}
    console.log("page, size, type, keyword : => " , queryObj)

    // movePage라는 기능을 props로 내려준다 
    const movePage = (num) => {
        console.log("Num", num)
        setSearch({page:num,size:queryObj.size,type:queryObj.type,keyword:queryObj.keyword})
    }

    return (  
      <div>
        Board List Page
        <ListComponent queryObj={queryObj} movePage={movePage}></ListComponent>
      </div>
    );
}

export default ListPage;
