import { useEffect, useState } from "react";

const ListSearchComponent = ({queryObj, moveSearch}) => {



    console.log("SearchComponent ==============")
    console.log("queryObj: => ",queryObj)

    const [searchObj, setSearchObj] = useState({type:'', keyword:''})

    // queryObj 가 바뀌면 한번만 랜더링 시킬거야 
    useEffect(() => {
        // searchhObj.type 이 없으면 '' 빈칸  ㅋ
        searchObj.type = queryObj.type || ''
        searchObj.keyword = queryObj.keyword || ''
        setSearchObj({...searchObj})
    },[queryObj])

    return ( 
        <div className="m-4 p-4 bg-blue-300 border-2">
            <select className="border1 m-2 p-2" 
            defaultValue={searchObj.type}

            // 검색조건이 있으면 {...searchObj} 로 변경 
            onChange={e => {
                searchObj.type = e.target.value
                setSearchObj({...searchObj})
            }}
            >
                <option value={'t'}>Title</option>
                <option value={'c'}>Content</option>
                <option value={'w'}>Writer</option>
                <option value={'tc'}>Title & Content</option>
                <option value={'tw'}>Title & Writer</option>
                <option value={'cw'}>Content & Writer</option>
                <option value={'ct'}>Content & Title</option>
                <option value={'tcw'}>Writer & Content & Title</option>
            </select>
            <input className="border1 m-2 p-2" type="text"
            value={searchObj.value}
            onChange={e => {
                searchObj.keyword = e.target.value
            }}
            ></input>
            <button className="border1 m-2 p-2">SEARCH</button>
        </div>
     );
}

export default ListSearchComponent;
