import { useEffect, useState } from "react"
import { getProduct } from "../../api/ProductApi"

const initState = {
    pno: 0,
    pname: '',
    pdesc: '',
    price: 0,
    images: []
}

const ModifyComponent = ({ pno, moveList, moveRead }) => {

    const [product, setProduct] = useState(initState)

    // useEffect 사용 pno 가 바뀔씨에만 렌더링 
    useEffect(() => {
        getProduct(pno).then(data => {
            setProduct(data)
            console.log(data)

        })
    }, [pno])


    return (
        <div>
            <div>Modiy Page</div>
            <div>
                <label>pname</label>
                {product.pname}
            </div>

            <div>
                <label>pdesc</label>
                {product.pdesc}
            </div>

            <div>
                <label>price</label>
                {product.price}
            </div>

            <div>
                <label>images</label>
                <ul className="list-none flex">
                    {/* 적당한 키가 없어서 idx 쓴다 */}
                    {product.images.map((fname, idx) => {
                        return (
                            <li key={idx} className="m-2">
                                <img src={`http://localhost/s_${fname}`} alt={fname} className="shadow-lg" />
                            </li>
                        );
                    })}
                </ul>
            </div>

            <div>
                <button className="bg-blue-500 text-lg" onClick={moveList}>Return List</button>
            </div>

        </div>
    );
}

export default ModifyComponent;
