import { useEffect, useState } from "react";
import ReplyList from "./ReplyList";

const initState = {
    bno :0,
    page :1,
    last :true
}

const ReplyWrapper = ({bno}) => {

    const [data, setData] = useState(initState)

    useEffect(() => {
        data.bno = bno; 
        data.last = true;
        data.page = 1;

        setData({...data})  // 상태 변경 

    }, [bno])   // bno 가 변경시 useEffect 처리 

    // move page data에 있는 page만 변경 
    const movePage = (num) => {
        data.page = num;
        data.last = false;
        setData({...data})
    }

    console.log(movePage)

    return ( 
        <div>
           <ReplyList {...data} movePage={movePage}></ReplyList>
        </div>
     );
}

export default ReplyWrapper;
