import { useEffect, useState } from "react";
import { deleteReply, getReply, putReply } from "../../api/ReplyAPI";

const initState = {
    rno: 0,
    bno: 0,
    replyText: '',
    replyFile: '',
    replyer: ''
}

const ReplyRead = ({ rno, cancleRead, refreshPage }) => {

    console.log(rno + " ReplyReadPage rno")

    const [reply, setReply] = useState(initState)

    useEffect(() => {
        getReply(rno).then(data => {
            console.log(data)
            setReply(data)
        })
    }, [rno])

    // 삭제를 위한 function parameter no 필요 프롭스 rno있음 
    const handleClickDelete = () => {
        deleteReply(rno).then(data => {
            console.log(data)
            alert(`${data.result}번 댓글이 삭제되었습니다`)
            refreshPage();
        })
    }

    // modify 를 위한 
    const handleChange = (e) => {
        reply[e.target.name] = e.target.value
        setReply({...reply})
    }

    const handleClickModify = () => {
        putReply(reply).then(data => {
            alert(`${data.result} 수정되엇습니다`)
            refreshPage();
        })
    }

    if(reply.replyText === '해당 글은 삭제되었습니다') {
        return <></>
    }

    return (
        <div className="m-8 bg-slate-600 border-2">
            <div>Reply REad {rno}</div>

            <div>
                <div>rno(댓글번호) : {rno}</div>
                <div><input type="text" name="replyText" onChange={handleChange} value={reply.replyText}>댓글내용</input></div>
            </div>

            <div>
                <button onClick={cancleRead}>취소</button>
            </div>

            <div>
                <button >modify</button>
            </div>

            <div>
                <button onClick={handleClickDelete}>delete</button>
            </div>

        </div>
    );
}

export default ReplyRead;
