import { useEffect, useState } from "react";
import { createSearchParams } from "react-router-dom";
import { getList } from "../../api/boardAPI";

const initState = {
    dtoList:[],
    end:0,
    start:0,
    next:false,
    prev:false,
    pageNums:[],
    page:0,
    size:0,
    requestDTO: null
}

const ListComponent = ({queryObj}) => {

    // list data 를 받아온다                // 초기값 설정 initState
    const [listData, setListData] = useState(initState)

    console.log(createSearchParams(queryObj).toString(), "<= createSearchParams(queryObj).toString()")

    useEffect(()=> {
        getList(queryObj).then(data => {
            console.log(data)
            setListData(data)
        })
    },[queryObj])

    return (
        <div>
            <div>LIST Component</div>
            <div>
                <ul>

                {listData.dtoList.map(({ bno, title , replyCount }) => ( <li key={bno}>
                         {bno} -  {title} = {replyCount}
                        </li>
                    ))}

                </ul>
                    {/* 페이지네이션 */}
                <div className="flex m-4 p-2">
                    <ul>
                        {listData.pageNums}
                    </ul>
                </div>

            </div>
        </div>
      );
}

export default ListComponent;
