import { useEffect, useState } from "react";
import { getRepliesOfBoard } from "../../api/ReplyAPI";
import ListPageComponent from "../../common/ListPageComponent";

const initState = {
    dtoList: [],
    end: 0,
    start: 0,
    next: false,
    prev: false,
    pageNums: [],
    page: 0,
    size: 0,
    requestDTO: null
}

const ReplyList = ({ bno, page, last, movePage }) => {

    console.log("Reply List ....." + bno)

    // list data 를 받아온다                // 초기값 설정 initState
    const [listData, setListData] = useState(initState)

    useEffect(() => {
        getRepliesOfBoard(bno, page, last).then(data => {
            console.log("data" + data)
            setListData(data)
        })
    }, [bno, page, last])

    return (
        <div>
            <div>ReplyListPAge</div>
            <div>
                <ul>
                    {listData.dtoList.map(reply => <li
                        key={reply.rno}>
                        {reply.rno} - {reply.replyText}
                        ㅇ아아아
                    </li>)}
                </ul>
                <ListPageComponent {...listData} movePage={movePage}></ListPageComponent>
            </div>
        </div>
    );
}

export default ReplyList;
