const ListPageComponent = ({ movePage, start, end, prev, next, pageNums, page }) => {


    const handleClickPage = (pageNum) => {
        movePage(pageNum)
    }


    return (
        <div className="flex m-4 p-2 justify-center">
            <ul className="flex">
                {/* 페이지네이션 start */}
                <div>

                    <ul className="flex m-4 p-2">

                        {/* 이전버튼 */}
                        {prev ? <li onClick={() => handleClickPage(start - 1)}>prev</li> : <></>}

                        {pageNums.map(num =>
                            <li className={`m-2 p-2 font-bold ${num === page ? 'bg-green-500 text-white' : 'bg-blue-300 text-white'}`} onClick={() => handleClickPage(num)} key={num}>
                                {num}
                            </li>)}

                        {/* 다음버튼 */}
                        {next ? <li onClick={() => handleClickPage(end + 1)}>next</li> : <></>}

                    </ul>

                </div>
                {/* 페이지네이션 stop */}
            </ul>
        </div>
    );
}

export default ListPageComponent;
