import { useEffect, useState } from "react";
import { createSearchParams } from "react-router-dom";
import { getList } from "../../api/boardAPI";
import ListPageComponent from "../../common/ListPageComponent";

const initState = {
    dtoList:[],
    end:0,
    start:0,
    next:false,
    prev:false,
    pageNums:[],
    page:0,
    size:0,
    requestDTO: null
}

const ListComponent = ({queryObj, movePage, moveRead}) => {

    // list data 를 받아온다                // 초기값 설정 initState
    const [listData, setListData] = useState(initState)

    console.log(createSearchParams(queryObj).toString(), "<= createSearchParams(queryObj).toString()")

    useEffect(()=> {
        getList(queryObj).then(data => {
            console.log(data)
            setListData(data)
        })
    },[queryObj])


    return (
        <div>
            <div>LIST Component</div>


            <div>
                <ul>

                {listData.dtoList.map(({ bno, title , writer, replyCount }) => ( <li key={bno} onClick={() => 
                moveRead={bno}} >
                         {bno} - {writer} - {title} - {replyCount}
                        </li>
                    ))}

                </ul>


            </div>

            <ListPageComponent movePage={movePage} {...listData}></ListPageComponent>

        </div>
      );
}

export default ListComponent;
