import { createSearchParams, useNavigate, useSearchParams } from "react-router-dom";
import BasicLayout from "../../layouts/BasicLayout";
import ListComponent from "../../components/board/ListComponent";
import ListSearchComponent from "../../components/board/ListSearchComponent";

// null Check Logic 
const checkNull = (obj) => {

  const result = {}

  for (const attr in obj) {
    const attrName = attr
    const attrValue = obj[attr]

    if (attrValue && attrValue !== 'null') {
      result[attrName] = attrValue
    }
  }

  return result
}

const ListPage = () => {

  // 쿼리스트링 처리를 위한 useSearchParams()
  const [search, setSearch] = useSearchParams()

  // 쿼리스트링 을 붙이기위한 
  const navigate = useNavigate()

  // api server에서 보내준 pageResponse를 확인하기 위한 search.get()
  const page = search.get("page") || 1
  const size = search.get("size") || 10
  const type = search.get("type") || null
  const keyword = search.get("keyword") || null

  // page size type keyword check 
  // checkNull
  const queryObj = checkNull({ page, size, type, keyword })
  console.log("page, size, type, keyword : => ", queryObj)

  // 페이지 이동 
  const movePage = (num) => {
    console.log("NUM ------------" + num)
    queryObj.page = num
    setSearch({ ...queryObj })
  }

  // search 
  const moveSearch = (type, keyword) => {
    queryObj.page = 1;
    queryObj.type = type;
    queryObj.keyword = keyword;
    setSearch({ ...queryObj })
  }

  const moveRead = (bno) => {
    console.log("moveRead : => ", bno)
    // read page go 
    const queryString = createSearchParams(queryObj).toString()
    navigate(`/board/read/${bno}?${queryString}`);
    // navigate(`/board/read/${bno}`, { search: `page=${3}` });
  }

  return (
    <div>
      Board List Page
      <ListSearchComponent queryObj={queryObj} moveSearch={moveSearch}></ListSearchComponent>
      <ListComponent queryObj={queryObj} movePage={movePage} moveRead={moveRead} ></ListComponent>
    </div>
  );
}

export default ListPage;
