const ListPageComponent = ({ movePage, start, end, prev, next, pageNums, page }) => {

    const handleClickPage = (pageNum) => {
        movePage(pageNum)
    }

    return (

        <div className="flex m-4 p-2 justify-center">
            <ul className="flex">

                {prev ? <li className="m-2 p-2 bg-blue-500 border-2 text-white font-bold"
                    onClick={() => handleClickPage(start - 1)}>PREV</li> : <></>}

                {pageNums.map(num =>
                    <li
                        className="m-2 p-2 bg-blue-500 border-2 text-white font-bold"
                        onClick={() => handleClickPage(num)}
                        key={num}>
                        {page === num ? <span className="text-black">{num}</span> : <span>{num}</span>}
                    </li>)}
                {next ? <li className="m-2 p-2 bg-blue-500 border-2 text-white font-bold"
                    onClick={() => handleClickPage(end + 1)}
                >NEXT</li> : <></>}
            </ul>
        </div>

    );
}

export default ListPageComponent;
