import { useEffect, useState } from "react"
import ReplyList from "./ReplyList"
import ReplyInput from "./ReplyInput"

/* 상태유지 */
const initState = {
    bno: 0,
    page: 1,
    last: true,
    refresh: false,
    current:0   // rno read 를 위함 
}

const ReplyWrapper = ({ bno }) => {
    const [data, setData] = useState(initState)

    // props의 상태를 유지할떄 useEffect => bno가 바뀔때 
    useEffect(() => {
        data.bno = bno
        data.last = true
        data.page = 1
        setData({ ...data })
    }, [bno])

    // 페이지가 변경되었을때 처리 함수
    const movePage = (num) => {
        console.log(num)

        data.page = num
        data.last = false
        data.refresh = !data.refresh
        setData({ ...data })
    }

    const refreshLast = () => {
        data.last = true;
        data.refresh = !data.refresh
        setData({...data})
    }

    // list 에서 read ㅇ를 띄워주기위함 
    const changeCurrent = (rno) => {
        data.current = rno
        setData({...data})
    }

    return (

        <div>
            <ReplyInput bno={bno} refreshLast={refreshLast}></ReplyInput>
            <ReplyList {...data} movePage={movePage}></ReplyList>
        </div>

    );
}

export default ReplyWrapper;
