import { useEffect, useState } from "react";
import { postReply } from "../../api/ReplyAPI";

const initState = {
    bno:0,
    replyText: '',
    replyFile: '',
    replyer: ''
}

const ReplyInput = ({bno, refreshLast}) => {

    const [reply, setReply] = useState(initState)

    // useEffect( () => {
    //     reply.bno = bno;
    //     setReply({...reply})
    // }, [bno])

    // event 시 reply의 내용물 바꿈 
    const handleChange = (e) => {
        reply[e.target.name] = e.target.value
        setReply({...reply})
    }

    // 
    const handleClickReigster = (e) => {
        reply.bno = bno
        postReply(reply).then(data => {
            console.log(data)
            alert(`${data.result} 번 댓글 등록 완료 `)
            refreshLast() 
            setReply({...initState})
        }) // {result :222}
    }


    return (
        <div className="m-8 bg-red-600 border-2">

            <div className="bg-white-500">Reply Input</div>

            <div>
                <label>ReplyText</label>
                <input type="text" name="replyText" value={reply.replyText} onChange={handleChange}></input>
            </div>


            <div className="m-4">
                <label>Replyer</label>
                <input type="text" name="replyer" value={reply.replyer} onChange={handleChange}></input>
            </div>

            <div className="m-10 bg-slate-200">
                <button onClick={handleClickReigster}>Register</button>
            </div>

        </div>
    );
}

export default ReplyInput;
