import { useEffect, useState } from "react";
import { getReply } from "../../api/ReplyAPI";

const initState = {
    rno:0,
    bno:0,
    replyText:'',
    replyFile:'',
    replyer:''
}

const ReplyRead = ({rno, cancleRead}) => {

    console.log(rno+" ReplyReadPage rno")

    const [reply,setReply] = useState(initState)

    useEffect( () => {
        getReply(rno).then(data => {
            console.log(data)
            setReply(data)
        })
    }, [rno])

    return ( 
        <div className="m-8 bg-slate-600 border-2">
            <div>Reply REad {rno}</div>

            <div>
            <div>rno(댓글번호) : {rno}</div>
            <div>replyText(댓글내용) : {reply.replyText}</div>
            </div>

            <button onClick={cancleRead}>취소</button>
            <button onClick={cancleRead}>modify</button>
            <button onClick={cancleRead}>delete</button>
        </div>
     );
}

export default ReplyRead;
