import axios from "axios";
import { getCookie } from "./cookieUtil";

// jwt 엑시오스를 사용하면 앞으로 모든 axios는 jwtAxios = axios.get 같은 jwtAxios
// 메소드를 사용해야한다 

const jwtAxios = axios.create()

const beforeReq = (config) => {

    console.log("beforeREQUEST=========")

    const { accessToken } = getCookie("login")

    if (!accessToken) {
        throw new Error("NO ACCESS TOKEN")
    }

    const header = {
        headers: {
            "Authorization": `Bearer ${accessToken}`,
            "Content-Type": "application/x-www-form-urlencoded;charset=utf-8",
        }
    }

    config.headers.Authorization = `Bearer ${accessToken}`

    return config

}

const requestFail = (err) => {
    console.log("request Fail ===========")
    return Promise.reject(err)
}

const beforeRes = (res) => {
    console.log("2XX Reponse .........")
    // 만료되었다면 
    if(res.data.error === 'Expired') {
        console.log("Access Token 만료")
        refreshJWT()
    }
    return res;
}

// jwt 토큰 리프레쉬 
const refreshJWT = () => {
    const{accessToekn, refreshToken} = getCookie("login")

    const header = {
        headers: {
            "Authorization": `Bearer ${accessToken}`
        }
    }
    axios.get(`http://localhost:8080/api/member/refreshToken=${refreshToken}`)
}


const responseFail = (err) => {
    console.log("response fail .......");
    return Promise.reject(err);
}

jwtAxios.interceptors.request.use(beforeReq, requestFail)
jwtAxios.interceptors.response.use(beforeReq, responseFail)

export default jwtAxios
