import { useEffect, useState } from "react";
import { createSearchParams } from "react-router-dom";
import { getList } from "../../api/boardAPI";

const initState = {
    dtoList:[],
    end:0,
    start:0,
    next:false,
    prev:false,
    pageNums:[],
    page:0,
    size:0,
    requestDTO: null
}

const ListComponent = ({queryObj, movePage}) => {

    // list data 를 받아온다                // 초기값 설정 initState
    const [listData, setListData] = useState(initState)

    console.log(createSearchParams(queryObj).toString(), "<= createSearchParams(queryObj).toString()")

    useEffect(()=> {
        getList(queryObj).then(data => {
            console.log(data)
            setListData(data)
        })
    },[queryObj])

    const handleClickPage = (pageNum) => {
        movePage(pageNum)
    }

    return (
        <div>
            <div>LIST Component</div>


            <div>
                <ul>

                {listData.dtoList.map(({ bno, title , writer, replyCount }) => ( <li key={bno}>
                         {bno} - {writer} - {title} - {replyCount}
                        </li>
                    ))}

                </ul>

                {/* 페이지네이션 start */}
                <div>

                    <ul className="flex m-4 p-2">

                        {/* 이전버튼 */}
                        {listData.prev ? <li onClick={()=> handleClickPage(listData.start-1)}>prev</li>:<></>}

                        {listData.pageNums.map(num => 
                        <li className="m-2 p-2 bg-blue-300 text-white font-bold" onClick={()=> handleClickPage(num) } key={num}>
                            {num}
                        </li>)}

                        {/* 다음버튼 */}
                        {listData.next ? <li onClick={() => handleClickPage(listData.end+1)}>next</li>:<></>}
                    </ul>

                </div>
                {/* 페이지네이션 stop */}

            </div>
        </div>
      );
}

export default ListComponent;
